s=input('Задайте формулу: ')
i,n,m,k=0,0,0,0
while i<len(s):
    if s[i]=='(':
        n+=1
        k+=1
    elif s[i]==')':
        n-=1
        m+=1
    i+=1
if n<0: print('Неверный порядок скобок')
elif n>0: print('Несовпадает количество скобок')
else: print('Все хорошо со скобками')
print('Открывающихся скобок:', k)
print('Закрывающихся скобок:', m)